import React from "react";
import "./MemberCard.css"
import {Button , Dropdown , SplitButton} from "react-bootstrap"
import {Link} from "react-router-dom"
import {useLocation} from "react-router-dom"


function MemberCard(props){
    let location = useLocation()
    
    let link = {
        pathname : "/memberpage" +  "/" + props._id,
        state : {
            project : location.state._id,
            member : {
                name : props.name,
                email : props.email,
                role : props.role,
                _id : props._id
            }

        }
    }

    const mainStyle = {
        display : "flex",
        justifyContent : "space-between",
        backgroundColor :'white',
        borderRadius : "15px",
        height : "60px",
        boxShadow : "10px 10px 8px 10px #888888;",
        marginTop : "10px",
        marginBottom : "10px"
        
    };

    function DeleteMember(){
        console.log( "Deleteing the member : " + props.name);
    }

    function DeleteConformation(){
        console.log("Alert! Are you sure you want to delete the member.")
        DeleteMember();
    }

    const DDButton = ()=>{ 
        return(
            <SplitButton title="Veiw" variant = "primary">
                <Dropdown.Item onClick={DeleteConformation}>Delete</Dropdown.Item>
            </SplitButton>
        )
    }
    return(

        <div style={mainStyle}>
            <div className="listcard-element">{props.name=="none"?<b>Name</b>:props.name}</div>
            <div className="listcard-element">{props.email=="none"?<b>Email</b>:props.email}</div>
            <div className="listcard-element">{props.role=="none"?<b>Role</b>:props.role}</div>
            <div className="listcard-element"><Link to= {link}>{props.name=="none"?"":<DDButton/>}</Link></div>
        </div>
    )
}

export default MemberCard
